                + "?merchantCount=" + "1"
                + "&oid=" + "BhJIHQidsZyHDRhy8f7dMPLSktNBIUxb%2F37EbzCgQS%2Bn8DQagexOtf6DOzmFXl0AoqA4WToBHUFsrQaBTZ7OP1JrRf%2Blp%2BNYVs6FnDMPnGNWqkAIkEpY5b9DEFh6suNDlcoylAEHvezul3GdoOTUDA%3D%3D"
                + "&asin=" + "B00J2LIFDC";

    A.ajax(url,{
            timeout: 10000,
            success : function(result) {
                if (typeof uet == 'function') {
                    // Record the critical feature
                    uet('cf', mbc_scope , {wb: 1});

                    // Send the data to client
                    uex( 'ld', mbc_scope, {wb: 1} );
                }
            },
            error: function(error) {
                if (typeof uet == 'function') {
                    // No cf is marked here. This can be tracked in PMET for an alarm
                    // Send the data to client
                    uex( 'ld', mbc_scope, {wb: 1} );
                }
            }
    });
});

