var appendPreloadParams = function(link,id,plSrc,styleTag) {
        if (typeof link.attr === 'function') {
          var src = link.attr("href");
          styleTag = encodeURIComponent(styleTag);
          link.attr("href",src+"&dpID="+id+"&dpSrc="+plSrc+"&preST="+styleTag);
        }
}

if(typeof P !== 'undefined'){

var getImageComponent = function(imgSrc){
    var imgComp = [];
    if(typeof imgSrc !== 'undefined' && typeof imgSrc === 'string'){
        var srcArray = imgSrc.split(/(.*\/[^\.]*)/i); // ["", "ecx.images-amazon.com/images/I/41mwctwVZfL", "_SX200_.jpg"]
        if(typeof srcArray !=='undefined' && srcArray.length > 2){
     imgComp['baseURL'] = srcArray[1];
     var pidArr = imgComp['baseURL'].split(/.*\//);
     if(typeof pidArr !== 'undefined' && pidArr.length > 1){
         imgComp['pid'] = pidArr[1];
     }
     var stAndExt = srcArray[2];
     if(typeof stAndExt !== 'undefined'){
         var stAndExtArr = stAndExt.split(".");
         if(typeof stAndExtArr !== 'undefined'){
      if(stAndExtArr.length == 2){
          imgComp['extension'] = stAndExtArr[1];
      }else if(stAndExtArr.length > 2){
          imgComp['st'] = stAndExtArr[1];
          imgComp['extension'] = stAndExtArr[2];
      }
         }
     }
        }
    }
    return imgComp;
}

P.when('A','ready').execute(function(A){
 var $=A.$;

 $('.p13n-asin').each(function(index,element){ 
     if(typeof element !== 'undefined'){

  var dpLink = $(element).find("a:first-child");
  if(typeof dpLink !== 'undefined'){ 
      var img = $(dpLink).find("img:first-child");
   if(typeof img !== 'undefined'){ 
       var src = $(img).attr('src');
       if(typeof src !== 'undefined'){ 
           var srcArray = getImageComponent(src);
           if(typeof srcArray !== 'undefined'){
        var baseURL = srcArray['baseURL'];
        var st = srcArray['st'];
        var extension = srcArray['extension'];
        var pid = srcArray['pid'];
        if( baseURL !== 'undefined' && extension !== 'undefined'){
                   appendPreloadParams(dpLink,pid,"sims",st);
        }
    }
       }
   }
  } 
      }
 });
});
}

