 $('#applyfilters').click(function() {
  var thisman = '.' + $('#moboman').val();
  var thisform = '.' + $('#moboform').val();
  if(thisman == '.null' && thisform != '.null') {
   $('.mobo').find('tr:not('+thisform+', .sectiontitle, .sectiondesc)').fadeOut(100, function() {
    $('.mobo').find(thisform).fadeIn(100);
   });
  }
  else if (thisman != '.null' && thisform == '.null') {
   $('.mobo').find('tr:not('+thisman+', .sectiontitle, .sectiondesc)').fadeOut(100, function() {
    $('.mobo').find(thisman).fadeIn(100);
   });
  }
  else if(thisman != '.null' && thisform != '.null') {
   $('.mobo').find('tr:not('+thisman+thisform+', .sectiontitle, .sectiondesc)').fadeOut(100, function() {
    $('.mobo').find(thisman+thisform).fadeIn(100);
    //$('.mobo').find(thisform+'not:('+thisman+')').fadeIn(100);
   });
  }
  else {
   $('.mobo').find('tr:not(.details)').fadeIn(100);
  }
 });
});

