      // Call this function when the page has been loaded
      function initialize() {
        var searchControl = new google.search.SearchControl();

  // site restricted web search
  var siteSearch = new google.search.WebSearch();
  siteSearch.setUserDefinedLabel("www.tonymacx86.com");
  siteSearch.setUserDefinedClassSuffix("siteSearch");
  siteSearch.setSiteRestriction("www.tonymacx86.com");
  searchControl.addSearcher(siteSearch);
 // enable AdSense
searchControl.enableAds('pub-9762329074683820');

  //Search API Topics
  searchControl.addSearcher(new google.search.WebSearch());
        searchControl.addSearcher(new google.search.NewsSearch());
  searchControl.addSearcher(new google.search.BlogSearch());
  searchControl.addSearcher(new google.search.ImageSearch());
  searchControl.addSearcher(new google.search.BookSearch());
  searchControl.addSearcher(new google.search.VideoSearch());
  //others
  //searchControl.addSearcher(new google.search.LocalSearch());
  //searchControl.addSearcher(new google.search.PatentSearch());

  // create a drawOptions object
  var drawOptions = new google.search.DrawOptions();
  // tell the searcher to draw itself in tabbed mode
  drawOptions.setDrawMode(google.search.SearchControl.DRAW_MODE_LINEAR);
 drawOptions.enableSearchboxOnly("http://www.tonymacx86.com/search.php", "googleSearch");
  searchControl.draw(document.getElementById("cse-search-form_header"), drawOptions);
      }
      google.setOnLoadCallback(initialize);

